import { useMousePosition } from "./hooks/useMousePosition";
import "./App.css";

function App() {
  const proximity = 10;
  const { x, y } = useMousePosition();
  const [center, setCenter] = useState({ x: 0, y: 0 });
  const ref = useRef(null);

  useEffect(() => {
    const centerOfButtonY =
      ref.current.offsetTop + ref.current.offsetHeight / 2;
    const centerOfButtonX =
      ref.current.offsetLeft + ref.current.offsetWidth / 2;

    console.log("centerOfButtonX", centerOfButtonX);
  }, [ref.current]);

  return (
    <div className="App">
      <button ref={ref} className="item">
        beans
      </button>
    </div>
  );
}

export default App;
