import { useState, useRef, useLayoutEffect } from "react";
import gsap, { Power4, Elastic } from "gsap";
import { useMousePosition } from "./hooks/useMousePosition";
import "./App.css";
const onEnter = (item, x, y) => {
  const magneticPullX = 0.4;
  const magneticPullY = 0.9;
  gsap.to(item, {
    x: x * magneticPullX,
    y: y * magneticPullY,
    ease: Power4.easeOut,
    duration: 0.4,
  });
};

const onLeave = (item) => {
  gsap.to(item, {
    x: 0,
    y: 0,
    ease: Elastic.easeOut.config(1.1, 0.5),
    duration: 0.7,
  });
};
function App() {
  const { x, y } = useMousePosition();
  const [center, setCenter] = useState({ x: 0, y: 0 });
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });
  const [isHovering, setIsHovering] = useState(false);
  const ref = useRef(null);
  let translate = "translate(0,0)";

  // const onEnter = (x, y) => {
  //   const magneticPullX = 0.4;
  //   const magneticPullY = 0.9;
  //   translate = `translate(${x * magneticPullX}px, ${y * magneticPullY}px)`;
  // };

  // const onLeave = () => {
  //   console.log("leave");
  //   translate = `translate(0px, 0px)`;
  // };
  useLayoutEffect(() => {
    const x = ref.current.offsetLeft + ref.current.offsetWidth / 2;
    const y = ref.current.offsetTop + ref.current.offsetHeight / 2;
    const width = ref.current.offsetWidth;
    const height = ref.current.offsetHeight;

    setCenter({ x, y });
    setDimensions({ width, height });
  }, [ref.current]);

  const dx = x - center.x;
  const dy = y - center.y;

  const distance = Math.sqrt(dx * dx + dy * dy);
  const hoverArea = isHovering ? 0.6 : 0.5;
  if (distance < dimensions.width * hoverArea) {
    if (!isHovering) {
      setIsHovering(true);
    }
    onEnter(ref.current, dx, dy);
    // onEnter(dx, dy);
  } else {
    if (isHovering) {
      onLeave(ref.current);
      // onLeave();
      setIsHovering(false);
    }
  }

  return (
    <div className="App">
      <button
        ref={ref}
        style={{
          transform: translate,
        }}
        className="item"
      >
        button
      </button>
    </div>
  );
}

export default App;
