import { useState, useRef, useEffect } from "react";
import gsap, { Power4, Elastic } from "gsap";
import { useMousePosition } from "../hooks/useMousePosition";

const onEnter = (item, x, y) => {
  const magneticPullX = 0.8;
  const magneticPullY = 0.9;
  gsap.to(item, {
    x: x * magneticPullX,
    y: y * magneticPullY,
    ease: Power4.easeOut,
    duration: 1,
  });
};

const onLeave = (item) => {
  gsap.to(item, {
    x: 0,
    y: 0,
    ease: Elastic.easeOut.config(1.1, 0.5),
    duration: 0.7,
  });
};

export default function MagneticWrapper({ children }) {
  const ref = useRef(null);
  const { x, y } = useMousePosition();
  const [center, setCenter] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });

  useEffect(() => {
    const { offsetTop, offsetLeft, offsetWidth, offsetHeight } = ref.current;
    const x = offsetLeft + offsetWidth / 2;
    const y = offsetTop + offsetHeight / 2;
    const width = offsetWidth;
    const height = offsetHeight;

    setCenter({ x, y });
    setDimensions({ width, height });
  }, [ref.current]);

  const dx = x - center.x;
  const dy = y - center.y;

  const distance = Math.sqrt(dx * dx + dy * dy);
  const hoverArea = isHovering ? 2 : 1; //hover area must be larger than not hovering.
  if (distance < dimensions.width * hoverArea) {
    //whichever is larger
    if (!isHovering) {
      setIsHovering(true);
    }
    onEnter(ref.current, dx, dy);
  } else {
    if (isHovering) {
      onLeave(ref.current);
      setIsHovering(false);
    }
  }

  return <div ref={ref}>{children}</div>;
}
